all_todos.map:
function(doc) { 
        if (doc.type == 'todo' && doc.active) { 
          emit(doc._id, [doc.title, doc.completed, doc.order]); 
        }
      }

views.map:
function(doc) { 
          if (doc.type == 'todo' && doc.active) { 
            emit(doc.user, [doc.title, doc.completed, doc.order]); 
          }
        }
